// Global type definition for fabric.js loaded via CDN
interface Window {
  fabric: {
    Canvas: any;
    StaticCanvas: any;
    Text: any;
    Image: any;
    Object: any;
    Rect: any;
    Circle: any;
    Triangle: any;
    Ellipse: any;
    Polygon: any;
    Path: any;
    Group: any;
    filters: {
      Grayscale: any;
      Sepia: any;
      Invert: any;
      Brightness: any;
      Contrast: any;
    }
  };
}